import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import "../adduser/add.css";
import toast from 'react-hot-toast';
import { backend_URL } from '../url.js';

const Edit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [user, setUser] = useState({ fname: "", lname: "", email: "" });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${backend_URL}/api/getOne/${id}`);
                setUser(response.data);
            } catch (error) {
                console.error('Error fetching user:', error);
                toast.error('Failed to fetch user details.', { position: 'top-right' });
            }
        };

        fetchData();
    }, [id]);

    const inputChangeHandler = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    };

    const validateForm = () => {
        if (!user.fname || !user.lname || !user.email) {
            toast.error('All fields are required.', { position: 'top-right' });
            return false;
        }
        return true;
    };

    const submitForm = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        try {
            const response = await axios.put(`${backend_URL}/api/update/${id}`, user);
            toast.success(response.data.msg, { position: "top-right" });
            navigate("/");
        } catch (error) {
            console.error('Error updating user:', error);
            toast.error('Failed to update user. Please try again.', { position: "top-right" });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='addUser'>
            <Link to={"/"}>Back</Link>
            <h3>Update Existing User</h3>
            <form className='addUserForm' onSubmit={submitForm}>
                <div className="inputGroup">
                    <label htmlFor="fname">First Name</label>
                    <input type="text" value={user.fname} onChange={inputChangeHandler} id="fname" name="fname" autoComplete='off' placeholder='First Name'/>
                </div>

                <div className="inputGroup">
                    <label htmlFor="lname">Last Name</label>
                    <input type="text" value={user.lname} onChange={inputChangeHandler} id="lname" name="lname" autoComplete='off' placeholder='Last Name'/>
                </div>

                <div className="inputGroup">
                    <label htmlFor="email">Email</label>
                    <input type="email" value={user.email} onChange={inputChangeHandler} id="email" name="email" autoComplete='off' placeholder='Email'/>
                </div>

                <div className="inputGroup">
                    <button type='submit' disabled={loading}>
                        {loading ? 'Updating...' : 'Update User'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default Edit;
