import React, { useEffect, useState } from 'react';
import axios from "axios";
import "../getuser/User.css";
import { Link } from 'react-router-dom';
import toast from "react-hot-toast";
import { backend_URL } from '../url.js'; 

export const User = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${backend_URL}/api/getAll`);
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
                toast.error('Failed to fetch users.', { position: 'top-right' });
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const deleteUser = async (userId) => {
        try {
            const response = await axios.delete(`${backend_URL}/api/delete/${userId}`);
            setUsers((prevUser) => prevUser.filter((user) => user._id !== userId));
            toast.success(response.data.msg, { position: 'top-right' });
        } catch (error) {
            console.error('Error deleting user:', error);
            toast.error('Failed to delete user.', { position: 'top-right' });
        }
    };

    return (
        <div className='userTable'>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    <Link to={"/add"} className='addButton'>Add User</Link>
                    <table border={1} cellPadding={10} cellSpacing={0} aria-label="User List">
                        <thead>
                            <tr>
                                <th>S.No.</th>
                                <th>User Name</th>
                                <th>User Email</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.length === 0 ? (
                                <tr>
                                    <td colSpan="4">No users found</td>
                                </tr>
                            ) : (
                                users.map((user, index) => (
                                    <tr key={user._id}>
                                        <td>{index + 1}</td>
                                        <td>{user.fname} {user.lname}</td>
                                        <td>{user.email}</td>
                                        <td className='actionButtons'>
                                            <button
                                                onClick={() => deleteUser(user._id)}
                                                aria-label={`Delete user ${user.fname} ${user.lname}`}
                                            >
                                                <i className="fa-solid fa-trash"></i>
                                            </button>
                                            <Link to={`/edit/${user._id}`} aria-label={`Edit user ${user.fname} ${user.lname}`}>
                                                <i className="fa-solid fa-pen-to-square"></i>
                                            </Link>
                                        </td>
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
}

export default User;
